mapper.select:
select out_id, out_time, file_path, verify_status, goods_id, agent_name, agent_phone, charge_name
from out_of_stock
where out_id = #{outId,jdbcType=VARCHAR}

select out_id, out_time, file_path, verify_status, goods_id, agent_name, agent_phone, charge_name
from out_of_stock
where verify_status = #{status,jdbcType=INTEGER}

select out_id, out_time, file_path, verify_status, goods_id, agent_name, agent_phone, charge_name
from out_of_stock
where verify_status = #{status,jdbcType=INTEGER}
and charge_name = #{chargeName,jdbcType=VARCHAR}
